import React from 'react';
import { MapContainer, TileLayer, Marker, Popup, CircleMarker } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fix for default marker icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

function Map({ soundscapeData }) {
  const center = [40.7128, -74.0060]; // New York City coordinates

  const getColor = (noiseLevel) => {
    if (noiseLevel < 50) return "green";
    if (noiseLevel < 70) return "yellow";
    return "red";
  };

  return (
    <div style={{ height: '400px', width: '100%', marginBottom: '20px' }}>
      <MapContainer center={center} zoom={12} style={{ height: '100%', width: '100%' }}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />
        {soundscapeData.map((data, index) => (
          <React.Fragment key={index}>
            <Marker 
              position={[
                center[0] + (index - 1) * 0.01, 
                center[1] + (index - 1) * 0.01
              ]}
            >
              <Popup>
                <strong>{data.location}</strong><br />
                Noise Level: {data.noise_level.toFixed(2)} dB
              </Popup>
            </Marker>
            <CircleMarker
              center={[
                center[0] + (index - 1) * 0.01, 
                center[1] + (index - 1) * 0.01
              ]}
              radius={10 + (data.noise_level / 10)}
              fillColor={getColor(data.noise_level)}
              color="black"
              weight={1}
              opacity={1}
              fillOpacity={0.7}
            >
              <Popup>
                <strong>{data.location}</strong><br />
                Noise Level: {data.noise_level.toFixed(2)} dB
              </Popup>
            </CircleMarker>
          </React.Fragment>
        ))}
      </MapContainer>
    </div>
  );
}

export default Map;
